'use strict';

// Set a global CONTEXT variable, 
// this is in node's context now
var CONTEXT = 'Node';


module.exports = {

    // Log the current context
    log: function () {
        // Log the new CONTEXT
        console.log(CONTEXT);
    },

    // returns the node context CONTEXT variable
    context: function () {
        return CONTEXT;
    }
};
